mapper.update:
update task_log where id = #{id}
set
                task_id = #{taskId},
                start_at = #{startAt},
                end_at = #{endAt},
                duration_seconds = #{durationSeconds}

mapper.select:
select * from task_log

    1 = 1

        AND id = #{id}


    AND task_id = #{taskId}


    AND start_at = #{startAt}


    AND end_at = #{endAt}


    AND duration_seconds  <= #{durationSeconds}
